<?php    function getTranslator($language,$ids,$db) {                $sql = "SELECT id, text FROM tab_language WHERE language = '".$language."' AND id IN (";        foreach ($ids as $value) {            $sql.= $value.",";        }        $sql = substr($sql, 0, -1).") ORDER BY id";        $result = mysql_query($sql,$db);                $arrReturn = array();        while ($resultLinha = mysql_fetch_array($result)) {            $arrReturn[$resultLinha['id']] = $resultLinha['text'];        }                return $arrReturn;    }?>