<?phpsession_start();include('../../includes/functions.php');$db = database();$usuario = trim(mysql_escape_string(trim($_POST['username'])));$senha = md5((mysql_escape_string(trim($_POST['password']))));$language = trim(mysql_escape_string(trim($_POST['language'])));$sql = "SELECT * FROM tab_user WHERE login = '" . $usuario . "' AND senha = '" . $senha . "'";$result = mysql_query($sql,$db) or die(mysql_error($db));if (mysql_num_rows($result)) {    $ajaxRetorno['statusRetorno'] = 'ok';    $_SESSION['hitpabxUser'] = $usuario;    $_SESSION['hitpabxLanguage'] = $language;} else {    $ajaxRetorno['statusRetorno'] = 'fail';    switch ($language) {        case "en":            $ajaxRetorno['msgdeerro'] = "Denied!";            break;        case "es":            $ajaxRetorno['msgdeerro'] = "Negado!";            break;        case "br":            $ajaxRetorno['msgdeerro'] = "Negado!";            break;        default:            break;    }}echo json_encode($ajaxRetorno);//echo json_encode(array("name"=>"John","time"=>"2pm"));?>